<style>
    @media (min-width: 1024px) {
        .dynamate-logo--absolute {
            position: absolute;
        }

        .dynamate-logo {
            width: 30px;
            transition: width 0.5s;
        }

        .dynamate-logo:hover,
        .dynamate-logo:focus {
            width: 125px;
        }
    }
</style>

